COPY (SELECT extract (year FROM fecha)::text as anio, extract (week FROM fecha)::text as semana, extract (dow FROM fecha) as dia, contaminante, max (medicion),  avg (medicion) FROM aire WHERE fecha >= '2013/01/01' GROUP BY extract (year FROM fecha), extract (week FROM fecha), extract (dow FROM fecha), contaminante ORDER BY semana, contaminante) TO '/tmp/contaminantes.csv' CSV HEADER
