SELECT extract (dow FROM fecha) as dia_semana, extract (quarter FROM fecha) as cuarto, extract (hour FROM fecha)::integer as parte_dia, contaminante, max (medicion), min (medicion), avg (medicion), stddev (medicion) FROM aire WHERE medicion > 0 GROUP BY extract (dow FROM fecha), extract (quarter FROM fecha),  extract (hour FROM fecha)::integer, contaminante ORDER BY contaminante, dia_semana, cuarto
